<template>
  <div :class="isMobileView ? 'data-container mobile-view' : 'data-container'">
    <!-- <div class="filters-container" v-if="registrations.length > 1"> -->
    <div v-if="!isMobileView" class="filters-container">
      <FiltersComponent
        @filter-apply="$emit('filter-apply', $event)"
        @clear-filters="$emit('clear-filters')"
      />
    </div>

    <div class="table-view-container">
      <div class="table-header">
        <h3>Registration <span>Data</span></h3>
        <div v-if="!isMobileView && registrations.length > 1" class="controls">
          <div class="dropdown" ref="sortDropdown">
            <label @click="toggleSortDropdown" for="my-dropdown" class="dropdown-label"
              >Sort By</label
            >
            <ul class="dropdown-menu" :class="{ 'is-active': isSortActive }">
              <li @click="sortData('A-Z')">Name (A-Z)</li>
              <li @click="sortData('Z-A')">Name (Z-A)</li>
              <li @click="sortData('O-Y')">Age (O-Y)</li>
              <li @click="sortData('Y-O')">Age (Y-O)</li>
            </ul>
          </div>
        </div>
      </div>

      <div v-if="stateDataView === 'tabular'" class="table-wrapper custom-scrollbar">
        <table v-if="registrations.length > 0">
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>D.O.B</th>
              <th>Email</th>
              <th>Mobile No.</th>
              <th>Exam Center</th>
              <th>Gender</th>
              <th>Subjects</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            <tr v-for="user in registrations" :key="user.id">
              <td>{{ user.fname }}</td>
              <td>{{ user.lname }}</td>
              <td>{{ user.dob }}</td>
              <td>{{ user.email }}</td>
              <td>{{ user.telenum }}</td>
              <td>{{ user.exam }}</td>
              <td class="capitalize">{{ user.gender }}</td>
              <td>{{ user.subjects.join(', ') }}</td>
              <td>
                <button
                  class="action-button edit-button"
                  @click="$emit('edit-registration', user.id)"
                >
                  <!-- Edit -->
                  <img src="../assets/edit-button.svg" alt="" style="height: 20px" />
                </button>
              </td>
              <td>
                <button
                  class="action-button delete-button"
                  @click="$emit('delete-registration', user.id)"
                >
                  <!-- Delete -->
                  <img src="../assets/delete-button.svg" alt="" style="height: 20px" />
                </button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
