<script>
import FiltersComponent from './FiltersComponent.vue'
import FiltersComponentMobile from './FiltersComponentMobile.vue'
import ProductCard from './ProductCard.vue'

export default {
  name: 'RegistrationData',
  components: {
    FiltersComponent,
    FiltersComponentMobile,
    ProductCard,
  },
  props: {
    registrations: {
      type: Array,
      required: true,
    },
    stateDataView: String,
    totalRegistrationsCount: {
      type: Number,
      required: true,
    },
    isFilterFnActive: {
      type: Boolean,
      required: true,
    },
    isSortFnActive: {
      type: Boolean,
      required: true,
    },
  },
  data() {
    return {
      isMobileView: false,
      activeOverlay: 'none', // to track only one filter is open at a time
      isSortActive: false,
    }
  },
  watch: {
    activeOverlay(newVal) {
      // prevent background scroliing on overlay
      if (newVal !== 'none') {
        document.body.style.overflow = 'hidden'
      } else {
        document.body.style.overflow = ''
      }
    },
    isSortActive(isActive) {
      if (isActive) {
        document.addEventListener('click', this.closeDropdownOutside)
      } else {
        document.removeEventListener('click', this.closeDropdownOutside)
      }
    },
  },
  methods: {
    closeDropdownOutside(event) {
      // ref is used at sortby to reference to sortby dom
      if (this.$refs.sortDropdown && !this.$refs.sortDropdown.contains(event.target)) {
        this.isSortActive = false
      }
    },
    sortData(sortType) {
      this.$emit('sort-data', sortType)
      this.isSortActive = false
    },

    // to handle Responsivness
    handleResize() {
      this.isMobileView = window.innerWidth <= 900
    },

    toggleSortDropdown() {
      this.isSortActive = !this.isSortActive
    },

    toggleOverlay(overlayName) {
      this.activeOverlay = this.activeOverlay === overlayName ? 'none' : overlayName
    },

    closeOverlay() {
      this.activeOverlay = 'none'
    },

    // filters and sort Mobile
    handleSortAndClose(sortType) {
      this.sortData(sortType)
      this.closeOverlay()
    },
    handleFilterApply(filters) {
      this.$emit('filter-apply', filters)
      this.closeOverlay()
    },

    handleClearFilters() {
      this.$emit('clear-filters')
      this.closeOverlay()
    },
  },
  // TODO M AND U
  mounted() {
    this.handleResize()
    window.addEventListener('resize', this.handleResize)
    document.removeEventListener('click', this.closeDropdownOutside)
  },
  beforeUnmount() {
    window.removeEventListener('resize', this.handleResize)
    document.removeEventListener('click', this.closeDropdownOutside)
  },
}
</script>

